import { Hono } from "hono";
import auth from "../route/auth.js";
import user from "../route/user.js";
import activity from "../route/activity.js";
import partner from "../route/partner.js";
import configuration from "../route/configuration.js";
import contract from "../route/contract.js";
import output from "../route/output.js";
import report from "../route/report.js";
const app = new Hono();
app.route("/auth", auth);
app.route("/users", user);
app.route("/outputs", output);
app.route("/activities", activity);
app.route("/partners", partner);
app.route("/configurations", configuration);
app.route("/contracts", contract);
app.route("/reports", report);
export default app;
